let links=document.querySelectorAll(".nav-list a");window.addEventListener("scroll",()=>{let e=window.scrollY+window.innerHeight/2;links.forEach(t=>{let l=document.querySelector(t.getAttribute("href"));if(l){let r=l.offsetTop,s=l.offsetHeight;e>=r&&e<r+s&&(links.forEach(e=>{e.parentElement.classList.remove("active")}),t.parentElement.classList.add("active"))}})});

// Modal functionality
const modal = document.getElementById('contactModal');
const btn = document.getElementById('contactBtn');
const closeBtn = document.getElementById('modalClose');

btn.onclick = function() {
    modal.style.display = "block";
}

closeBtn.onclick = function() {
    modal.style.display = "none";
}

window.onclick = function(event) {
    if (event.target == modal) {
        modal.style.display = "none";
    }
}